//боковое меню
const menu 			= document.getElementById("nav__burger-checked-js");
const navigation 	= document.getElementById("nav-js");
const class_burger 	= document.getElementsByClassName("nav__burger__line-js");

menu.addEventListener("click", ()=>{
	class_burger[0].classList.toggle("nav__burger__line-open-top");
	class_burger[1].classList.toggle("nav__burger__line-open-middle");
	class_burger[2].classList.toggle("nav__burger__line-open-top");

	navigation.classList.toggle("nav-open");
});

