
let start_nav   = document.querySelector('.start-nav');
let a           = start_nav.getElementsByClassName('start__link');

/*Получаем изначальные позиции (в px)*/
let st_data = [];
GetStartPosImg();

start_nav.addEventListener("mousemove", cursorMovement, false);

/*отслеживаем движение курсора*/
function cursorMovement (event) {
        let st = getComputedStyle(event.target);
        let text = event.target.innerHTML;

        let posX = st.getPropertyValue('background-position-X');
        let posY = st.getPropertyValue('background-position-Y');

        posX = GetCount(posX);
        posY = GetCount(posY);

        let x = +st_data[text].st_x - (event.pageX / 25) + "px";
        let y = +st_data[text].st_y - (event.pageY / 25) + "px";

        event.path[0].style.backgroundPositionX = x;
        event.path[0].style.backgroundPositionY = y;
}

function GetCount(s) {
        let b = "";
        for(let i = 0; i< s.length; i++){
                if(!(s[i] >= '0' && s[i] <= '9') && s[i] != '-')
                        break;
                b += s[i];
        }
        return b;
}

function GetStartPosImg() {
       for(let i = 0; i< a.length; i++){
                let st = getComputedStyle(a[i]);
                let text = a[i].innerHTML;

                let x = st.getPropertyValue('background-position-X');
                let y = st.getPropertyValue('background-position-Y');

                x = GetCount(x);
                y = GetCount(y);

                st_data[text] = {
                        st_x : x,
                        st_y : y
                }
        }
}
