//Боковое меню
let menu_checkbox 	= document.querySelector('#checkbox-menu-js'),
	menu 			= document.querySelector('#active-menu-js'),
	nav 			= document.querySelector('#nav-js');

menu.addEventListener('click' , ()=>{
	if(!menu_checkbox.checked){
		nav.classList.remove('nav-close-js');
		nav.classList.add('nav-open-js');
	}else{
		nav.classList.remove('nav-open-js');
		nav.classList.add('nav-close-js');
	}
});
