//Боковое меню
let menu_checkbox 	= document.querySelector('#checkbox-menu-js'),
    menu 		= document.querySelector('#active-menu-js'),
    nav 		= document.querySelector('#nav-js');

menu.addEventListener('click' , ()=>{
	let class_list_nav  = nav.classList,
	    class_list_menu = menu.classList;

	if(!class_list_nav[1])
		class_list_nav.add('nav-open-js');
	else if(class_list_nav[1] === 'nav-open-js'){
		class_list_nav.remove('nav-open-js');
		class_list_nav.add('nav-close-js');
	}else if(class_list_nav[1] === 'nav-close-js'){
		class_list_nav.remove('nav-close-js');
		class_list_nav.add('nav-open-js');
	};

	!class_list_menu[2] ? class_list_menu.add('open_burger-js') : class_list_menu.remove('open_burger-js');
});
